// Widget: btnLimpiarFiltroChart -> onClick
(async () => {
    // 1. Limpia el filtro "Mis Tareas"
    await storeValue('filtroMisTareasActivo', false); 

    // 2. Ejecución de Resets para limpiar selecciones de filtros y gráficos (CRÍTICO)
    await resetWidget("selEstado", true); 
    await resetWidget("selCliente_filtro", true); 
    await resetWidget("selAsignadoA_filtro", true);

    // --- RESET DE LOS 4 CHARTS PARA ACTUALIZAR DATA ---
    await resetWidget("chartAtrasados", true);  // 1. Atrasados
    await resetWidget("chartEnTiempo", true);   // 2. Por entregar (Flujo Activo)
		await resetWidget("Chart3", true);          // 3. Por aprobar 
    await resetWidget("chartPorCobrar", true);  // 4. Por cobrar
    // ------------------------------------------------

    // 3. Refresca las fuentes de datos (para asegurar que los gráficos tengan data fresca)
    // Se asume que estos queries alimentan los charts directamente o a través de utilidades.
    await getPedidosParaCharts.run(); 
    await getPedidosPendientesPorColabor.run();
    await getPedidosPorCobrar.run(); 

    // 4. Refresca la tabla principal (para aplicar el filtro vacío)
    await getPedidosColaborador.run();

    // 5. Para asegurar que la caché de Atrasados se rompa
    await storeValue('lastChartUpdate', Date.now()); 
})();
