progress:
{{
  (() => {
    // 1. Obtener el ID del estado calculado por tu función utils
    const estadoId = utils.calcularNuevoEstadoId();

    // 2. Si el ID no está listo (devuelve undefined o null), devolvemos 0
    //    ¡Esta es la verificación que arregla el error NaN!
    if (!estadoId) {
      return 0;
    }

    // 3. Buscar el objeto de estado completo
    const estadoObj = getEstadosOrdenados.data.find(e => e.id == estadoId);

    // 4. Si no encontramos el objeto (porque getEstadosOrdenados no está listo), devolvemos 0
    if (!estadoObj) {
      return 0;
    }

    // 5. Obtener el orden_visual
    const orden = estadoObj.orden_visual;
    const maxOrden = 11;

    // 6. Si el orden es nulo o 0, devolvemos 0
    if (!orden || orden === 0) {
      return 0;
    }

    // 7. Si es el máximo, devolvemos 100
    if (orden === maxOrden) {
      return 100;
    }

    // 8. Calcular, redondear y devolver el entero
    return Math.round((orden / maxOrden) * 100);
  })()
}}
