// Widget: btnGuardarcambios -> onClick
(async () => {
    try {
        await updatePedidoEditable.run(); // Guarda la nueva fecha de plazo

        // --- AÑADIDO: Ejecución de Queries de Gráficos ---
        await getPedidosParaCharts.run(); // Fuente de Atrasados / EnTiempo
        await getPedidosPendientesPorColabor.run(); // Fuente de Por aprobar
        await getPedidosPorCobrar.run();             // Fuente de Por cobrar

        // --- Refresco y Reset de la UI ---
        await getPedidoDetalle.run(); 
        await getPedidosColaborador.run(); 

        await resetWidget("chartAtrasados", true);
        await resetWidget("chartEnTiempo", true);  
        await resetWidget("Chart3", true);
        await resetWidget("chartPorCobrar", true); 

        await storeValue('lastChartUpdate', Date.now()); // Para romper la caché

        showAlert('¡Cambios guardados exitosamente!', 'success');

    } catch (error) {
        showAlert('Error al guardar los cambios: ' + error.message, 'error');
    }
})();
