onClick:
{{
(async () => {
    await insertNuevoPedido.run();

    // 1. Ejecuta la consulta de la tabla principal
    await getPedidosColaborador.run(); 

    // 2. Ejecuta la consulta de los gráficos (CRÍTICO)
    await getPedidosParaCharts.run();

    // 3. Forzar un repintado/reseteo de los gráficos después de la actualización (opcional, pero recomendado)
    await resetWidget("chartAtrasados", true);
    await resetWidget("chartEnTiempo", true);
    await resetWidget("Chart3", true);
	    await resetWidget("chartPorCobrar", true);

    // 4. Cierra el modal y notifica
    closeModal(modNuevoPedido.name);
    showAlert('Pedido creado con éxito.', 'success');
})();
}}
