onClick:
{{
  (async () => {
    try {
      // --- INICIO: INTENTAR GUARDAR Y REFRESCAR ---

      // Paso 1: Ejecuta la consulta que guarda los cambios y espera.
      await updatePedidoEditable.run();

      // Paso 2: Si el guardado tuvo éxito, refresca los datos.
      await getPedidoDetalle.run(); 
      await getPedidosColaborador.run(); 

      // Paso 3: Muestra alerta de éxito.
      showAlert('¡Cambios guardados exitosamente!', 'success');

    } catch (error) {
      // --- INICIO: MANEJO DE ERRORES ---

      // Paso 4: Si CUALQUIERA de los 'await' en el 'try' falla, se ejecutará esto.
      showAlert('Error al guardar los cambios: ' + error.message, 'error');

    }
  })();
}}
